
public class AddNum {

	int Sub(int x,int y)
	{
		return x-y;
	}
	
	static float sub(int x,float y)
	{
		return x-y;
	}
	
	static double sub (int x, double y)
	{
		return x-y;
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		sub(2,5);
		sub(2,7.5);
		sub(8,6.2);
		
	}

}
